glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec4 v_color;
in vec2 v_uv0;
in vec2 v_uv1;
uniform sampler2D texture;
float Remap01(float a, float b, float t) {
  return (t-a) / (b-a);
}
float Remap(float a, float b, float c, float d, float t) {
  return Remap01(a, b, t) * (d-c) + c;
}
void main()
{
  vec2 uv = v_uv0.xy;
  vec4 col = texture(texture, uv) * v_color;
  vec2 o = uv * 2.0 - 1.0;
  float angle = atan(o.y, o.x);
  angle = Remap(-3.141592653589793, 3.141592653589793, 0., 1.0, angle);
  angle = fract(angle + cc_time.x * 0.02);
  col.a = smoothstep(0.1, 0.01, abs(col.a - 0.90));
  float mask = fract(angle * 30.0);
  mask = step(0.4, mask);
  col.a *= mask;
  gl_FragColor = col;
}

