glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = vec2(a_uv0.x, 1.0-a_uv0.y);
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform ARGS {
  float blur;
  float t;
  vec2 xyRatio;
};
mat2 R2(float angle) {
  float c = cos(angle), s = sin(angle);
  return mat2(c, -s, s, c);
}
void main()
{
  vec2 uv = v_uv0.xy;
  uv = uv * 2.0 - 1.0;
  uv *= xyRatio;
  float len = length(uv);
  float angle = 3.14159265359 / 4.;
  uv = R2(angle) * uv;
  float offset = (t-0.5) * 2.828 * (xyRatio.x + xyRatio.y) * 0.5 * 1.05;
  float wave = sin(uv.x * 3. * 3.14159265359) * 0.05 + sin(uv.x * 0.98 * 3.14159265359) * 0.05 + offset;
  float alpha = 1.0 - smoothstep(wave, wave + blur, uv.y);
  vec3 col = v_color.rgb;
  gl_FragColor = vec4(col, alpha);
}

