glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec4 v_color;
in vec2 v_uv0;
in vec2 v_uv1;
uniform sampler2D texture;
uniform ARGS {
  float yRatio;
  float sdfRatio;
  float outlineHalfWidth;
  float time;
};
float Remap01(float a, float b, float t) {
  return (t-a) / (b-a);
}
float Remap(float a, float b, float c, float d, float t) {
  return Remap01(a, b, t) * (d-c) + c;
}
float CircleSDF(vec2 uv, vec2 o, float r, float sdfRatio) {
  float s = Remap(r+sdfRatio*0.5, r-sdfRatio*0.5, 0., 1., distance(uv, o));
  return clamp(s, 0., 1.);
}
float Noise(vec2 p) {
  return fract(sin(
    dot(p, vec2(12.9898,78.233))
  ) * 43758.5453123);
}
void main()
{
  vec2 uv = v_uv0.xy;
  vec4 col = texture(texture, uv) * v_color;
  vec2 o = uv * 2.0 - 1.0;
  o.y *= yRatio;
  float alpha = col.a;
  float centerAlpha = 0.5;
  float t = time;
  float spike = Noise(floor(o * 10.));
  float mask1 = CircleSDF(o, vec2(0., 0.), 0.5 + (spike-0.5) * t, sdfRatio);
  alpha += (spike-0.5) * t;
  float outlineAlphaDist = outlineHalfWidth;
  float mask = smoothstep(outlineAlphaDist, outlineAlphaDist - 0.02, abs(alpha - centerAlpha));
  float ring = mask;
  vec4 outlineColor = vec4(1., 1., 1., ring);
  col = outlineColor;
  gl_FragColor = col;
}

