glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_uv0.y = 1.0 - v_uv0.y;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform UARGS {
  vec4 foreColor;
  vec2 repeat;
  float time;
};
void main () {
  vec2 grid_uv = fract(v_uv0 * repeat);
  grid_uv.x = fract(grid_uv.x - time * 0.2);
  grid_uv.y = 1.0 - fract(grid_uv.y + sin(time) * 0.1);
  vec4 color = texture(texture, grid_uv);
  color.rgb = mix(v_color.rgb, foreColor.rgb, color.a);
  color.a = 1.0;
  gl_FragColor = color;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute vec2 a_uv0;
varying vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_uv0.y = 1.0 - v_uv0.y;
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
varying vec2 v_uv0;
uniform sampler2D texture;
uniform vec4 foreColor;
uniform vec2 repeat;
uniform float time;
void main () {
  vec2 grid_uv = fract(v_uv0 * repeat);
  grid_uv.x = fract(grid_uv.x - time * 0.2);
  grid_uv.y = 1.0 - fract(grid_uv.y + sin(time) * 0.1);
  vec4 color = texture2D(texture, grid_uv);
  color.rgb = mix(v_color.rgb, foreColor.rgb, color.a);
  color.a = 1.0;
  gl_FragColor = color;
}
