glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_uv0.y = v_uv0.y;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D drop;
float Hash (float x)
{
  return fract (sin (x * 21.34) * 456.78);
}
vec2 GetDrops(vec2 uv, float t, float speedMultiplier)
{
    vec2 gridSize = vec2 (10.0, 4.0);
    float sizeOffset = texture (drop, uv * 0.1).x;
    float speed = ((Hash (floor (uv * gridSize).x) + 1.0) * 0.5)  * .2;
    uv.y += speed * t * speedMultiplier;
    vec2 st = fract (uv * gridSize + vec2 (sizeOffset * 0.5, sizeOffset * 0.1));
    st -= 0.5;
    st *= 2.0;
    vec2 p = (st)/gridSize;
    float mainDropDist = smoothstep (0.03, 0.02, length (p));
    return ((p * mainDropDist * 3.5));
}
void main()
{
    vec2 uv = v_uv0.xy;
    float timex = cc_time.x;
    vec2 drops = GetDrops (uv, timex + 21.5, .75);
    drops += GetDrops (uv * 2.0, timex + 251.5, 1.75) * .5;
    gl_FragColor = texture (texture, v_uv0 + drops);
    gl_FragColor.a = smoothstep(0.0, 0.01, length(drops));
}

