glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
in vec2 a_uv1;
in vec2 a_uv2;
in float a_shine;
out vec2 v_uv0;
out vec2 v_uv1;
out vec2 v_uv2;
out float f_edge;
out float f_shine;
void main() {
  vec4 pos = vec4(a_position, 1);
  f_edge = pos.z;
  pos.z = 0.;
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_uv1 = a_uv1;
  v_uv2 = a_uv2;
  f_shine = a_shine;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
in vec2 v_uv0;
in vec2 v_uv1;
in vec2 v_uv2;
in float f_edge;
in float f_shine;
uniform sampler2D texture;
uniform sampler2D bgTexture;
void main()
{
  vec2 uv = v_uv0.xy;
  vec2 uv1 = v_uv1.xy;
  vec2 uvstep = v_uv2.xy;
  uv -= uvstep;
  vec4 up = texture(texture, uv);
  uv = uvstep * 2. + uv;
  vec4 down = texture(texture, uv);
  vec4 mask = texture(texture, v_uv0.xy);
  vec4 shine = mask;
  vec4 bgcol = texture(bgTexture, uv1);
  bgcol.a = mask.a;
  mask.rgb = bgcol.rgb;
  vec4 old = mask;
  mask.rgb *= clamp(down.a, 0.5, 1.0);
  mask.rgb += (1.-up.a) * vec3(1.0);
  float x = length(mask.rgb - old.rgb);
  mask.a *= smoothstep(0., 1.0, x);
  mask = mix(mask, vec4(1.0, 1.0, 1.0, shine.r * shine.a), f_shine);
  gl_FragColor = mix(bgcol, mask, f_edge);
  gl_FragColor.a *= v_color.a;
}

