glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = vec2(a_uv0.x, 1.0-a_uv0.y);
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform ARGS {
  float blur;
  float startTime;
  vec2 xyRatio;
};
float sdHexagon(in vec2 p, in float r)
{
  const vec3 k = vec3(-0.866025404,0.5,0.577350269);
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}
void main()
{
  vec2 uv = v_uv0.xy;
  uv = uv * 2.0 - 1.0;
  uv *= xyRatio;
  float deltaTime = max(cc_time.x - startTime, 0.);
  float t = fract(deltaTime * 0.1);
  t = sqrt(t);
  float alpha = smoothstep(0., 0.7, sin(t*3.14159265359));
  float mask = sdHexagon(uv.yx, t);
  mask = smoothstep(blur, 0., abs(mask-0.1));
  float decay = 1. - 0.03*alpha;
  vec3 col = mix(v_color.rgb, v_color.rgb * decay, mask);
  gl_FragColor = vec4(col, v_color.a);
}

