glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
in vec2 a_uv1;
out vec2 v_uv0;
out float f_edge;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos.z = 0.;
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  f_edge = a_uv1.x;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
in vec2 v_uv0;
in float f_edge;
uniform sampler2D texture;
uniform YSTEP {
  float yStep;
};
void main()
{
  vec2 uv = v_uv0.xy;
  uv.y -= yStep;
  vec4 up = texture(texture, uv);
  uv.y = yStep * 2. + uv.y;
  vec4 down = texture(texture, uv);
  vec4 col = texture(texture, v_uv0.xy);
  vec4 old = col;
  col.rgb *= clamp(down.a, 0.6, 1.0);
  col.rgb += (1.-up.a) * vec3(1.0);
  float x = length(col.rgb - old.rgb);
  col.a *= smoothstep(0., 1.0, x);
  gl_FragColor = mix(old, col, f_edge);
}

